import { Component } from '@angular/core';

@Component({
  selector: 'app-searchbar',
  templateUrl: './searchbar.component.html',
  styleUrls: ['./searchbar.component.css']
})
export class SearchbarComponent {
  isSearchDropdownOpen: boolean = false;

  toggleSearchDropdown() {
    this.isSearchDropdownOpen = !this.isSearchDropdownOpen;
  }
}
<div class="input-group">
  <input type="text" class="form-control" placeholder="Search for products brands and more...">
  <div class="input-group-append">
    <span class="input-group-text" (click)="toggleSearchDropdown()">
      <i class="material-icons">search</i>
    </span>
  </div>
</div>

<div class="collapse" [ngClass]="{'show': isSearchDropdownOpen}" id="searchDropdown">
  <div class="dropdown-content">
    <input type="text" class="form-input" placeholder="Search for products brands and more...">
    <!-- Additional dropdown content can be added here -->
  </div>
</div>
