import { Component } from '@angular/core';

@Component({
  selector: 'app-banner',
  templateUrl: './banner.component.html',
  styleUrls: ['./banner.component.css'],
})
export class BannerComponent {
  bannerData = [
    {
      id: 1,
      url: 'https://rukminim1.flixcart.com/flap/3376/560/image/d117a62eb5fbb8e1.jpg?q=50',
    },
    {
      id: 2,
      url: 'https://rukminim1.flixcart.com/flap/3376/560/image/57267a180af306fe.jpg?q=50',
    },
    {
      id: 3,
      url: 'https://rukminim1.flixcart.com/flap/3376/560/image/ae9966569097a8b7.jpg?q=50',
    },
    {
      id: 4,
      url: 'https://rukminim1.flixcart.com/flap/3376/560/image/f6202f13b6f89b03.jpg?q=50',
    }
  ];

}
<div id="Banner">
  <div class="big-box">
    <p-carousel class="p-carousel-indicators" [value]="bannerData" [autoplayInterval]="2000"
      [styleClass]="'custom-carousel'">
      <ng-template let-images pTemplate="item">
        <div class="product-item">
          <div class="product-item-content">
            <img [src]="images.url" style="background-repeat: no-repeat;" width="100%" height="100%"
              [alt]="images.alt">
          </div>
        </div>
      </ng-template>
    </p-carousel>
  </div>
</div>
