import { Component } from '@angular/core';
import { Router } from '@angular/router';
@Component({
  selector: 'app-account',
  templateUrl: './account.component.html',
  styleUrls: ['./account.component.css']
})
export class AccountComponent {
  constructor(private router: Router) {}

  isDropdownOpen = false;
  toggleDropdown() {
    this.isDropdownOpen = !this.isDropdownOpen;
  }

}
<div class="account">
  <div class="account-icon" (mouseenter)="toggleDropdown()" (mouseleave)="toggleDropdown()">
    <span class="material-symbols-outlined">
      person
    </span>
    <span class="account-text">Account</span>
  </div>
  <div class="account-dropdown" [ngClass]="{'open': isDropdownOpen}" (mouseleave)="toggleDropdown()">
    <button routerLink="/authentication/login" >Login</button>
    <button routerLink="/authentication/register">Register</button>
    <button>Wishlist</button>
    <button>Your Orders</button>
    <button>Your Referrals</button>
  </div>
</div>
