import { Component } from '@angular/core';

@Component({
  selector: 'app-navbar',
  templateUrl: './navbar.component.html',
  styleUrls: ['./navbar.component.css'],
})
export class NavbarComponent {
  isMenuOpen: boolean = false;
  isSubMenuOpen: boolean = false;
  isAdminVisible: boolean = true;

  checkAdminVisibility() {
    this.isAdminVisible = true;
  }
  toggleMenu() {
    this.isMenuOpen = !this.isMenuOpen;
    this.isSubMenuOpen = false;
  }
  toggleSubMenu() {
    this.isSubMenuOpen = !this.isSubMenuOpen;
  }

}
<nav class="navbar navbar-expand-lg navbar-light bg-light">
  <button class="navbar-toggler .ml-2 position-static .d-none .d-sm-block .d-md-none" type="button"
    data-toggle="collapse" data-target="#navbarCollapse" aria-controls="navbarCollapse" aria-expanded="false"
    aria-label="Toggle navigation" (click)="toggleMenu()">
    <span class="navbar-toggler-icon"></span>
  </button>
  <ul class="navbar-nav d-lg-none .d-md-none">
    <li class="nav-item">
      <app-searchbar></app-searchbar>
    </li>
  </ul>
  <a class="navbar-brand" routerLink="">
    <img src="../../assets/app-logo.png" class="image" alt="Logo" height="60" width="120%">
  </a>
  <ul class="navbar-nav" id="flex">
    <li class="nav-item-1">
      <app-searchbar></app-searchbar>
    </li>
    <li class="nav-item-2">
      <app-account></app-account>
    </li>
    <li class="nav-item-3">
      <app-cart></app-cart>
    </li>
    <li class="nav-item-4" routerLink="/authentication/admin" *ngIf="isAdminVisible">
      <div>
        <span class="material-symbols-outlined">
          admin_panel_settings
        </span>
        <span class="admin-text">Admin</span>
      </div>
    </li>
  </ul>
</nav>
